import React, { useEffect, useState } from 'react'
import { v4 as uuidv4 } from 'uuid'
import Nav from './nav'

import './index.css'
// 轮播图函数
export default function SlideShow(lists) {
  const [num, setNum] = useState(0)
  const [img, setImg] = useState(Nav)
  console.log(Nav);
  // 轮播图模块
  const slideShow = () => {
    //若搜索不到信息，返回空串
    if (lists.lists.length === 0) return ''
    else {
      return (
        <>
          {img.map((imgObj, index) => {
            return (
              <img className='signal-img' key={imgObj.id} src={imgObj.url} style={{ display: (index === num ? (imgObj.name === 'phone' ? 'block' : 'none') : 'none') }} />
            )
          })
          }
          <ul ul className='slide-circle' >
            {img.map((imgObj, index) => {
              return (
                <li key={imgObj.id} className={index === num ? (imgObj.name === 'phone' ? 'active' : '') : ''}></li>
              )
            })
            }
          </ul>
        </>
      )
    }
  }

  useEffect(() => {
    // 设置定时器，每隔2s切换图片
    const timer = setInterval(() => {
      const str = img.filter((item) => item === 'phone')
      console.log(str);
      setNum(index => {
        if (index === img.length - 1) return 0
        else return index + 1
      })
    }, 2000)
    // 在函数执行结束之后销毁定时器
    return () => clearInterval(timer)
  }, [num])
  return (
    <div className='slide-show'>
      <div className='slide-img'>
        {slideShow()}
      </div>
    </div >
  )
}
