import React, { useEffect, useState } from 'react'
import axios from 'axios'
import './index.css'
import {
  ShoppingCartOutlined,
} from '@ant-design/icons'
export default function Home() {
  const [productList, setPorductList] = useState([])
  const [num, setNum] = useState(0)
  const [least, setLeast] = useState(false)
  // 更改商品数量
  const changeNum = (e) => {

  }
  // 检测商品数量num变化的钩子
  useEffect(() => {
    // 判断商品数量是否为0，决定-按钮是否禁用
    if (num === 0) {
      setLeast(true)
    } else {
      setLeast(false)
    }
  }, [num])

  // 执行获取商品信息的函数
  const getProduct = () => {
    axios.get('https://hmajax.itheima.net/api/news').then(result => {
      setPorductList(result.data.data);
    })
  }

  // 只调用一次商品信息
  useEffect(getProduct, [])

  // 添加事件委托，实现单个商品数量的增减
  const updateMsg = (e) => {
    console.log(e.target.className);
    // 判断点击的按钮是-或者+，执行不同的更新操作
    if (e.target.className.includes('sub') && num > 0) {
      setNum(num - 1)
    }
    if (e.target.className.includes('add')) {
      setNum(num + 1)
    }
  }
  return (
    <div>
      <h1><ShoppingCartOutlined />&nbsp;购物车</h1>
      <ul className='product-show'>
        {
          productList.map(item => {
            return <li key={item.id}>
              <img src={item.cmtcount} />
              {item.source}
              <span id={item.id} onClick={updateMsg} >
                <button className='product-num sub' onClick={changeNum} disabled={least}>-</button>
                <input type="text" className='product-num' value={num} defaultValue={''} />
                <button className='product-num add' onClick={changeNum}>+</button>
              </span>
            </li>
          })
        }
      </ul>
    </div>
  )
}
