import React, { useEffect, useState } from 'react'
import { v4 as uuidv4 } from 'uuid'
import { Flex, Radio } from 'antd';
import './index.css'
// 轮播图
export default function SlideShow() {
  const [num, setNum] = useState(0)
  const [img, setImg] = useState([
    { id: uuidv4(), url: require('../../../assets/img/1.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/2.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/1.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/2.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/1.jpg') },
  ])

  const imgList = img.map((imgObj, index) => <img className='signal-img' key={imgObj.id} src={imgObj.url} style={{ display: (index === num) ? 'block' : 'none' }} />)
  const point = img.map((imgObj, index) => <li key={imgObj.id} style={{ listStyleType: 'circle' }} className={index === num ? 'selected' : ''}>11</li>)

  useEffect(() => {
    const timer = setInterval(() => {
      setNum(index => {
        console.log(index);
        if (index === img.length - 1) return 0
        else return index + 1
      })
    }, 2000)
    return () => clearInterval(timer)
  }, [num])
  return (
    <div className='slide-show'>
      <div className='slide-img'>
        {imgList}
        <ul>
          {point}
        </ul>
      </div>

    </div>
  )
}
