import React, { useState, useEffect } from 'react'
import SlideShow from './SlideShow'
import { SearchOutlined } from '@ant-design/icons'
import Nav from './nav'
import './index.css'
export default function Home() {
  // Hook
  const [lists, setLists] = useState([])
  // 初始化搜索框输入数据
  const [input, setInput] = useState('')
  const handleChange = (e) => {
    // 更新value
    setInput(e.target.value)
  }
  useEffect(() => {
    // 筛选数据
    setLists(() => {
      if (!input) return Nav
      else return Nav.filter(item => {
        const str = item.title.includes(input)
        console.log(str);
        return str === null ? '抱歉！暂无相关商品' : item.title.includes(input)
      })
    })
  }, [input])
  return (
    <div>
      <div className="search">
        <i><SearchOutlined /></i>
        <input type="text" value={input} placeholder='请输入关键词' onChange={(e) => handleChange(e)} />
      </div>
      {/* 顶部导航 */}
      <ul className='header-nav'>
        {
          lists.map(item => {
            return (
              <li key={item.id} className='home-list'>
                <img src={item.url} alt="" />
                <p>{item.title}</p>
              </li>
            )
          })
        }
      </ul>
      <SlideShow />
    </div >
  )
}
