import React, { useEffect, useState } from 'react'
import { v4 as uuidv4 } from 'uuid'

import './index.css'
// 轮播图
export default function SlideShow() {
  const [num, setNum] = useState(0)
  const [img, setImg] = useState([
    { id: uuidv4(), url: require('../../../assets/img/phone1.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/phone2.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/phone3.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/phone4.jpg') },
  ])
  const slideShow = (lists) => {
    if (this.props.lists)
      return (
        <>
          {img.map((imgObj, index) => <img className='signal-img' key={imgObj.id} src={imgObj.url} style={{ display: (index === num) ? 'block' : 'none' }} />)}
          <ul ul className='slide-circle' >
            {img.map((imgObj, index) => <li key={imgObj.id} className={index === num ? 'active' : ''}></li>)}
          </ul>
        </>
      )
  }


  useEffect(() => {
    const timer = setInterval(() => {
      setNum(index => {
        if (index === img.length - 1) return 0
        else return index + 1
      })
    }, 2000)
    return () => clearInterval(timer)
  }, [num])
  return (
    <div className='slide-show'>
      <div className='slide-img'>
        {slideShow}
      </div>
    </div >
  )
}
