import React, { useState } from 'react'
import { v4 as uuidv4 } from 'uuid'
import 'index.css'
// 轮播图
export default function SlideShow() {
  const [img, setImg] = useState([
    { id: uuidv4(), url: require('../../../assets/img/1.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/1.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/1.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/1.jpg') },
    { id: uuidv4(), url: require('../../../assets/img/1.jpg') },
  ])

  const imgList = img.map((imgObj, index) => <img className='signal-img' key={imgObj.id} src={imgObj.url} style={{ display: (index === imgObj.length) ? 'block' : 'none' }} />)
  return (
    <div className='slide-show'>
      <div className='slide-img'>
        {imgList}
      </div>
    </div>
  )
}
