import React, { useState, useEffect } from 'react'
import SlideShow from './SlideShow'
import { SearchOutlined } from '@ant-design/icons'
import Nav from './nav'
import './index.css'
export default function Home() {
  // Hook
  const [lists, setLists] = useState([])
  // 初始化搜索框输入数据
  const [input, setInput] = useState('')
  const handleChange = (e) => {
    // 更新value
    setInput(e.target.value)
  }

  useEffect(() => {
    // 筛选数据
    setLists(() => Nav.filter(item => item.title.includes(input)))
  }, [input])
  // 根据检索信息渲染数据
  const searchLists = () => {
    // 若检索为空，返回提示信息
    if (lists.length === 0) return <h1 style={{ color: "rgb(152, 152, 152)" }}>抱歉！没有相关信息</h1>
    else {
      return (lists.map(item => {
        return (
          <li key={item.id} className='home-list'>
            <img src={item.url} alt="" />
            <p>{item.title}</p>
          </li>
        )
      })
      )
    }
  }
  return (
    <div>
      <div className="search">
        {/* 搜索模块 */}
        <i><SearchOutlined /></i>
        <input type="text" value={input} placeholder='请输入关键词' onChange={(e) => handleChange(e)} />
      </div>
      {/* 顶部导航 */}
      <ul className='header-nav'>
        {/* 渲染商品信息 */}
        {searchLists()}
      </ul>
      <SlideShow lists={lists} />
    </div >
  )
}
