import React, { useState } from 'react'
import { v4 as uuidv4 } from 'uuid'
import SlideShow from './SlideShow'
import { SearchOutlined } from '@ant-design/icons'
import './index.css'
export default function Home() {
  // Hook
  const [lists, setLists] = useState([
    { id: uuidv4(), url: require('../../assets/img/phone2.jpg'), title: '手机' },
    { id: uuidv4(), url: require('../../assets/img/appliance5.jpg'), title: '家电' },
    { id: uuidv4(), url: require('../../assets/img/1.jpg'), title: '男装' },
    { id: uuidv4(), url: require('../../assets/img/1.jpg'), title: '女装' },
    { id: uuidv4(), url: require('../../assets/img/1.jpg'), title: '生活' },
  ])
  // 初始化搜索框输入数据
  const [input, setInput] = useState('')
  const handleChange = (e) => {
    // 更新value
    setInput(e.target.value)
    // 筛选数据
    setLists((msgObj) => {
      if (!input) return msgObj
      else return msgObj.filter(item => item.title.includes(input))

    })
  }
  return (
    <div>
      <div className="search">
        <i><SearchOutlined /></i>
        <input type="text" value={input} placeholder='请输入关键词' onChange={(e) => handleChange(e)} />
      </div>
      {/* 顶部导航 */}
      <ul className='header-nav'>
        {
          lists.map(item => {
            return (
              <li key={item.id} className='home-list'>
                <img src={item.url} alt="" />
                <p>{item.title}</p>
              </li>
            )
          })
        }
      </ul>
      <SlideShow />
    </div >
  )
}
